import{_ as a,c as e,o as i,a4 as n}from"./chunks/framework.CL14adID.js";const g=JSON.parse('{"title":"Angel A. Urbina","description":"","frontmatter":{},"headers":[],"relativePath":"CVSpanish.md","filePath":"CVSpanish.md"}'),o={name:"CVSpanish.md"},r=n('<h1 id="angel-a-urbina" tabindex="-1">Angel A. Urbina <a class="header-anchor" href="#angel-a-urbina" aria-label="Permalink to &quot;Angel A. Urbina&quot;">​</a></h1><h3 id="changemaker-engineer-from-ideas-to-action" tabindex="-1">Changemaker | Engineer | From Ideas to Action <a class="header-anchor" href="#changemaker-engineer-from-ideas-to-action" aria-label="Permalink to &quot;Changemaker | Engineer | From Ideas to Action&quot;">​</a></h3><hr><h2 id="resumen" tabindex="-1">Resumen <a class="header-anchor" href="#resumen" aria-label="Permalink to &quot;Resumen&quot;">​</a></h2><p><strong>Ingeniero Industrial</strong> y <strong>Científico de datos</strong> con una década de experiencia en liderazgo de ONG, ofrezco una perspectiva única que combina técnicas avanzadas con una visión humanista. Mi dominio en la aplicación de la <strong>Inteligencia Artificial</strong> en el sector industrial, apoyado por una sólida base en ciencia de datos y habilidades de programación en Python, R, y Rust, me capacita para enfrentar y resolver retos complejos. He demostrado mis habilidades de comunicación y persuasión en <strong>intervenciones públicas y parlamentarias</strong>. Mi profundo conocimiento en <strong>SAP</strong> y mi experiencia en la optimización de sistemas <strong>ERP</strong> y <strong>Procesos Industriales</strong> en Repsol demuestran mi capacidad para mejorar procesos, aumentar eficiencias y liderar proyectos técnicos.</p><hr><h2 id="experiencia" tabindex="-1">Experiencia <a class="header-anchor" href="#experiencia" aria-label="Permalink to &quot;Experiencia&quot;">​</a></h2><h4 id="repsol-www-repsol-com" tabindex="-1">REPSOL | <a href="http://www.repsol.com" target="_blank" rel="noreferrer">www.repsol.com</a> <a class="header-anchor" href="#repsol-www-repsol-com" aria-label="Permalink to &quot;REPSOL | [www.repsol.com](http://www.repsol.com)&quot;">​</a></h4><p><strong>Gestión Almacenes e Inventario Integrado. [Ene 2009-Presente]</strong></p><ul><li>Especialización en optimización de sistemas ERP y SAP, centrándose en la racionalización de procesos industriales y gestión de inventario.</li><li>Experiencia en equilibrar eficazmente las necesidades de tecnología de la información e industria, aprovechando la adaptabilidad y el conocimiento técnico en SAP y ciencia de Datos para mejorar la eficiencia operativa en entornos complejos.</li></ul><h4 id="salut-mental-catalunya-salut-mental-catalunya" tabindex="-1">Salut Mental Catalunya | <a href="https://salutmental.org/" target="_blank" rel="noreferrer">Salut Mental Catalunya</a> <a class="header-anchor" href="#salut-mental-catalunya-salut-mental-catalunya" aria-label="Permalink to &quot;Salut Mental Catalunya | [Salut Mental Catalunya](https://salutmental.org/)&quot;">​</a></h4><p><strong>Vicepresidente</strong></p><ul><li>Experiencia en comité directivo de ONGs, liderando estratégicamente hacia objetivos definidos.</li><li>Capacidad de comunicación efectiva y networking con partes interesadas clave. Capacidad de análisis político sólido para influir en cambios legislativos y desarrollo de advocacy convincente.</li><li>Habilidad probada en planificación estratégica y gestión de proyectos para alcanzar resultados.</li></ul><hr><h2 id="educacion" tabindex="-1">Educación <a class="header-anchor" href="#educacion" aria-label="Permalink to &quot;Educación&quot;">​</a></h2><ul><li><p><strong>M.Sc. en Ciencia de Datos, (UOC, 2024)</strong></p><ul><li>Proyecto: &quot;Aplicación de la Inteligencia Artificial Generativa para la Identificación de Necesidades y Resolución de Problemas en la Lactancia Materna.&quot; Desarrollado para la empresa <a href="https://lactapp.es/" target="_blank" rel="noreferrer">LactApp</a>.</li><li>Temas Clave: Modelos de Lenguaje, Bases de Datos de Grafos, Extracción de Conocimiento.</li></ul></li><li><p><strong>PDD, Liderazgo e Innovación Social, (ESADE, 2017)</strong></p><ul><li><a href="https://youtu.be/na3Jzx2TEf4?si=mkMKF0LJdYnDUC2s" target="_blank" rel="noreferrer">Explicación Programa</a></li><li>Temas Clave: Innovación, Liderazgo, Comunicación Efectiva, Negociación , Impacto Social.</li></ul></li><li><p><strong>M.Sc. Ingeniero Industrial, (Universidad de Sevilla, ETSII 1996)</strong></p></li></ul><hr><h2 id="competencias-no-tecnicas" tabindex="-1">Competencias No Técnicas <a class="header-anchor" href="#competencias-no-tecnicas" aria-label="Permalink to &quot;Competencias No Técnicas&quot;">​</a></h2><ul><li><strong>Comunicación Efectiva:</strong> Habilidad para articular conceptos complejos de manera clara ante una variedad de audiencias.</li><li><strong>Mente Abierta:</strong> Adaptabilidad y receptividad a nuevas ideas y métodos de resolución de problemas.</li><li><strong>Oratoria:</strong> Competente en la entrega de presentaciones convincentes, mejorando la colaboración y el intercambio de conocimientos.</li></ul><h2 id="competencias-tecnicas" tabindex="-1">Competencias Técnicas <a class="header-anchor" href="#competencias-tecnicas" aria-label="Permalink to &quot;Competencias Técnicas&quot;">​</a></h2><h4 id="ciencia-de-datos-y-analisis" tabindex="-1">Ciencia de Datos y Análisis <a class="header-anchor" href="#ciencia-de-datos-y-analisis" aria-label="Permalink to &quot;Ciencia de Datos y Análisis&quot;">​</a></h4><ul><li>Especialista en estadística, aprendizaje automático y minería de datos, con destrezas avanzadas en Python, R, SQL, y Visualización Datos.</li><li>Profundo conocimiento en modelos de lenguaje y su aplicación en el análisis de texto para extraer información crítica y tendencias de grandes volúmenes de datos no estructurados.</li><li>Experiencia en el desarrollo de soluciones basadas en IA, como chatbots y asistentes virtuales, adaptados a mejorar la interacción y los procesos dentro de entornos reales.</li></ul><h4 id="mejora-de-procesos-industriales-y-aplicacion-de-ia" tabindex="-1">Mejora de Procesos Industriales y Aplicación de IA <a class="header-anchor" href="#mejora-de-procesos-industriales-y-aplicacion-de-ia" aria-label="Permalink to &quot;Mejora de Procesos Industriales y Aplicación de IA&quot;">​</a></h4><ul><li>Implementación de metodologías Lean y Six Sigma para optimización de procesos, reducción de costos y mejora de calidad, complementado con la integración de soluciones de IA para automatización y toma de decisiones.</li><li>Habilidad para dirigir equipos multidisciplinarios en la adopción de tecnologías avanzadas, asegurando la alineación con los objetivos estratégicos y la transformación digital de la organización.</li><li>Innovación en el uso de modelos de lenguaje avanzados para la automatización y mejora de procesos industriales, aumentando la eficiencia, capacidad de producción y seguridad.</li></ul><hr><h2 id="ejemplos-de-fortaleza-clave" tabindex="-1">Ejemplos de Fortaleza Clave <a class="header-anchor" href="#ejemplos-de-fortaleza-clave" aria-label="Permalink to &quot;Ejemplos de Fortaleza Clave&quot;">​</a></h2><p>Vídeos</p><ul><li><a href="https://youtu.be/0CUdsXlIllE?si=BRVdAjMIGiWAALsX" target="_blank" rel="noreferrer">Entrevista </a></li><li><a href="https://youtu.be/2_WZ6tdqVX4?si=CD-UrkUDO-i3RknZl" target="_blank" rel="noreferrer">Intervención Parlamentaria</a></li><li><a href="https://youtu.be/OP7Pw6Hhbeg?si=_MdgL7viNx6R_4zJ" target="_blank" rel="noreferrer">Entrevista Para Campaña Obertament 2023</a></li></ul><h2 id="varios" tabindex="-1">Varios <a class="header-anchor" href="#varios" aria-label="Permalink to &quot;Varios&quot;">​</a></h2><ul><li><a href="/guide/GP2022.html">Informe automatización GP2024</a>.</li><li><a href="/guide/Proyectos.html">Simplificación y automatización de procesos</a>.</li></ul>',30),t=[r];function s(l,c,d,p,u,m){return i(),e("div",null,t)}const b=a(o,[["render",s]]);export{g as __pageData,b as default};
