import{_ as a,c as e,o as t,a4 as d}from"./chunks/framework.CL14adID.js";const f=JSON.parse('{"title":"Listado Parametros claves en la planificación MRP de SAP","description":"","frontmatter":{},"headers":[],"relativePath":"guide/Planificacion_SAP.md","filePath":"guide/Planificacion_SAP.md"}'),i={name:"guide/Planificacion_SAP.md"},r=d('<h1 id="listado-parametros-claves-en-la-planificacion-mrp-de-sap" tabindex="-1">Listado Parametros claves en la planificación MRP de SAP <a class="header-anchor" href="#listado-parametros-claves-en-la-planificacion-mrp-de-sap" aria-label="Permalink to &quot;Listado Parametros claves en la planificación MRP de SAP&quot;">​</a></h1><table><thead><tr><th>Nombre del Parámetro</th><th>Parámetro</th><th>Descripción</th><th>Propósito</th></tr></thead><tbody><tr><td>MRP Type</td><td>MRP Tipo</td><td>Determina el método de planificación y reabastecimiento del material</td><td>Controla si el material se reabastece mediante producción o compra, y cómo se planifica.</td></tr><tr><td>MRP Group</td><td>Grupo de MRP</td><td>Configuración que agrupa materiales con criterios de planificación similares</td><td>Facilita la gestión y configuración de materiales que deben ser planificados de manera similar.</td></tr><tr><td>MRP Controller</td><td>Controlador de MRP</td><td>Responsable de la planificación de materiales dentro de un área específica</td><td>Asigna el responsable de planificación para una serie de materiales, facilitando la organización y responsabilidad.</td></tr><tr><td>Lot Size</td><td>Tipo de Lote</td><td>Define cómo se calculan los tamaños de lote en los pedidos de material</td><td>Optimiza la cantidad de material pedido, minimizando el costo o maximizando la eficiencia de fabricación.</td></tr><tr><td>Minimum Stock Level</td><td>Nivel de Stock Mínimo</td><td>Cantidad mínima de inventario que debe mantenerse en todo momento</td><td>Previene la ruptura de stock, asegurando que siempre haya material disponible para la producción o venta.</td></tr><tr><td>Reorder Point</td><td>Punto de Pedido</td><td>Nivel de inventario que activa un nuevo pedido de reabastecimiento</td><td>Automatiza el proceso de pedido para asegurar un flujo constante de materiales sin interrupciones de producción.</td></tr><tr><td>Replenishment Quantity</td><td>Cantidad de Reaprovisionamiento</td><td>Cantidad estándar a ordenar cada vez que se alcanza el punto de pedido</td><td>Simplifica el proceso de pedido al establecer una cantidad predeterminada para reordenar.</td></tr><tr><td>Planning Horizon</td><td>Horizonte de Planificación</td><td>Periodo futuro para el cual se realiza la planificación de material</td><td>Define el rango temporal para la planificación de la producción y la compra, asegurando visibilidad a largo plazo.</td></tr><tr><td>Planning Cycle</td><td>Periodicidad</td><td>Frecuencia con la que se ejecuta la planificación MRP</td><td>Determina cuán a menudo se actualiza y recalcula la planificación de necesidades de materiales.</td></tr><tr><td>Safety Stock</td><td>Stock de Seguridad</td><td>Cantidad adicional de inventario mantenida para mitigar el riesgo de ruptura de stock</td><td>Proporciona un colchón contra fluctuaciones en la demanda o retrasos en el suministro, asegurando operaciones continuas.</td></tr><tr><td>Lead Time</td><td>Plazo de Entrega</td><td>Tiempo requerido desde el pedido hasta la recepción del material</td><td>Importante para calcular cuándo ordenar materiales para cumplir con las fechas de producción sin mantener inventario excesivo.</td></tr><tr><td>Forecasting Model</td><td>Modelo de Pronóstico</td><td>Modelo estadístico utilizado para predecir la demanda futura basada en datos históricos</td><td>Permite una planificación más precisa al estimar las necesidades futuras de materiales basadas en tendencias y patrones pasados.</td></tr><tr><td>Scrap Percentage</td><td>Porcentaje de Desperdicio</td><td>Porcentaje estimado de material que se desperdiciará durante la producción</td><td>Ajusta la cantidad de material necesario para pedidos, considerando la pérdida esperada durante la fabricación.</td></tr></tbody></table>',2),n=[r];function o(c,l,s,p,m,u){return t(),e("div",null,n)}const _=a(i,[["render",o]]);export{f as __pageData,_ as default};
